package com.example.shoppingcart.repositories;

import androidx.lifecycle.LiveData;
import androidx.lifecycle.MutableLiveData;

import com.example.shoppingcart.models.Product;

import java.util.ArrayList;
import java.util.List;
import java.util.UUID;

public class ShopRepo {

    private MutableLiveData<List<Product>> mutableProductList;

    public LiveData<List<Product>> getProducts() {
        if (mutableProductList == null) {
            mutableProductList = new MutableLiveData<>();
            loadProducts();
        }
        return mutableProductList;
    }

    private void loadProducts() {
        List<Product> productList = new ArrayList<>();
        productList.add(new Product(UUID.randomUUID().toString(), "Shirt", 29, true, "data:image/jpeg;base64," ));
        productList.add(new Product(UUID.randomUUID().toString(), "Pant", 29, true, "data:image/jpeg;base64,"));
        productList.add(new Product(UUID.randomUUID().toString(), "Coat", 39, true, "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSZKFCXq7yxdWM7zUxb52Bi4KpjEsdJixs9DA&usqp=CAU"));
        productList.add(new Product(UUID.randomUUID().toString(), "Other shirt", 29, false, "data:image/jpeg;base64,"));
        productList.add(new Product(UUID.randomUUID().toString(), "Another Coat", 39, true, "data:image/jpeg;base64,"));

        mutableProductList.setValue(productList);
    }
}
